import React, { useState, useEffect } from "react";
import "./AddFruits.css";
import axios from "axios";

export default function AddFruits() {
    const [products, setProducts] = useState([]);
    const [formVisible, setFormVisible] = useState(false);
    const [editMode, setEditMode] = useState(false);
    const [currentProduct, setCurrentProduct] = useState(null);
    const [formData, setFormData] = useState({
        itemName: "", quantity: "", unit: "", productDetails: "",
        refillDate: "", status: "", notes: ""
    });
    const [errors, setErrors] = useState({});

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8074/category");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.itemName.trim()) newErrors.itemName = "Item name is required.";
        if (!formData.quantity.trim() || isNaN(formData.quantity) || formData.quantity <= 0) newErrors.quantity = "Quantity must be a positive number.";
        if (!formData.unit.trim()) newErrors.unit = "Unit is required.";
        if (!formData.productDetails.trim()) newErrors.productDetails = "Product details are required.";
        if (!formData.refillDate.trim()) newErrors.refillDate = "Refill date is required.";
        if (!formData.status.trim()) newErrors.status = "Status is required.";
        if (!formData.notes.trim()) newErrors.notes = "Notes are required.";
        return newErrors;
    };

    const sendData = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        try {
            if (editMode) {
                await axios.put(`http://localhost:8074/category/update/${currentProduct._id}`, formData);
                alert("Product Updated Successfully");
            } else {
                await axios.post("http://localhost:8074/category/add", formData);
                alert("New Product Added Successfully");
            }
            setFormVisible(false);
            setEditMode(false);
            fetchProducts();
        } catch (err) {
            alert(err.response?.data?.message || "An error occurred. Please try again.");
        }
    };

    const handleEdit = (product) => {
        setFormData(product);
        setCurrentProduct(product);
        setFormVisible(true);
        setEditMode(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this product?")) {
            try {
                await axios.delete(`http://localhost:8074/category/delete/${id}`);
                alert("Product Deleted Successfully");
                fetchProducts();
            } catch (error) {
                alert("Error deleting product");
            }
        }
    };

    return (
        <div className="container">
            <h2>Product List</h2>
            <table>
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Quantity</th>
                        <th>Unit</th>
                        <th>Product Details</th>
                        <th>Refill Date</th>
                        <th>Status</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product, index) => (
                            <tr key={index}>
                                <td>{product.itemName}</td>
                                <td>{product.quantity}</td>
                                <td>{product.unit}</td>
                                <td>{product.productDetails}</td>
                                <td>{product.refillDate}</td>
                                <td>{product.status}</td>
                                <td>{product.notes}</td>
                                <td>
                                    <button onClick={() => handleEdit(product)}>Edit</button>
                                    <button onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="8">No products available</td>
                        </tr>
                    )}
                </tbody>
            </table>

            <button onClick={() => { setFormVisible(!formVisible); setEditMode(false); setFormData({ itemName: "", quantity: "", unit: "", productDetails: "", refillDate: "", status: "", notes: "" }); }}>
                {formVisible ? "Cancel" : "+ Add Product"}
            </button>

            {formVisible && (
                <form onSubmit={sendData}>
                    <h2>{editMode ? "Edit Product" : "Add Product"}</h2>

                    <label>Item Name:</label>
                    <input type="text" name="itemName" value={formData.itemName} onChange={handleChange} />
                    {errors.itemName && <p className="error">{errors.itemName}</p>}

                    <label>Quantity:</label>
                    <input type="text" name="quantity" value={formData.quantity} onChange={handleChange} />
                    {errors.quantity && <p className="error">{errors.quantity}</p>}

                    <label>Unit:</label>
                    <input type="text" name="unit" value={formData.unit} onChange={handleChange} />
                    {errors.unit && <p className="error">{errors.unit}</p>}

                    <label>Product Details:</label>
                    <input type="text" name="productDetails" value={formData.productDetails} onChange={handleChange} />
                    {errors.productDetails && <p className="error">{errors.productDetails}</p>}

                    <label>Refill Date:</label>
                    <input type="date" name="refillDate" value={formData.refillDate} onChange={handleChange} />
                    {errors.refillDate && <p className="error">{errors.refillDate}</p>}

                    <label>Status:</label>
                    <input type="text" name="status" value={formData.status} onChange={handleChange} />
                    {errors.status && <p className="error">{errors.status}</p>}

                    <label>Notes:</label>
                    <input type="text" name="notes" value={formData.notes} onChange={handleChange} />
                    {errors.notes && <p className="error">{errors.notes}</p>}

                    <button type="submit">{editMode ? "Update" : "Submit"}</button>
                </form>
            )}
        </div>
    );
}
