import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AddFruits.css";

export default function AddFruits() {
    const [products, setProducts] = useState([]);
    const [formData, setFormData] = useState({ itemname: "", quantity: "", unit: "", productDetails: "", refillDate: "", status: "", notes: "" });
    const [editId, setEditId] = useState(null);
    const [formVisible, setFormVisible] = useState(false);

    useEffect(() => { fetchProducts(); }, []);
    const fetchProducts = async () => {
        try { const res = await axios.get("http://localhost:8074/category"); setProducts(res.data); }
        catch (error) { console.error("Error fetching products:", error); }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editId) {
                await axios.put(`http://localhost:8074/category/update/${editId}`, formData);
            } else {
                await axios.post("http://localhost:8074/category/add", formData);
            }
            resetForm(); fetchProducts();
        } catch (err) { alert("An error occurred."); }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8074/category/delete/${id}`);
            fetchProducts();
        } catch (err) { alert("Error deleting product."); }
    };

    const handleEdit = (product) => {
        setFormData(product);
        setEditId(product._id);
        setFormVisible(true);
    };

    const resetForm = () => {
        setFormData({ itemname: "", quantity: "", unit: "", productDetails: "", refillDate: "", status: "", notes: "" });
        setEditId(null);
        setFormVisible(false);
    };

    return (
        <div className="container">
            <h2>Product List</h2>
            <table>
                <thead>
                    <tr>
                        <th>Item Name</th><th>Quantity</th><th>Unit</th><th>Details</th><th>Refill Date</th><th>Status</th><th>Notes</th><th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product._id}>
                            <td>{product.itemname}</td><td>{product.quantity}</td><td>{product.unit}</td><td>{product.productDetails}</td>
                            <td>{product.refillDate}</td><td>{product.status}</td><td>{product.notes}</td>
                            <td>
                                <button onClick={() => handleEdit(product)}>Edit</button>
                                <button onClick={() => handleDelete(product._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={() => setFormVisible(!formVisible)}>{formVisible ? "Cancel" : "Add Product"}</button>
            {formVisible && (
                <form onSubmit={handleSubmit}>
                    <input type="text" placeholder="Item Name" value={formData.itemname} onChange={(e) => setFormData({ ...formData, itemname: e.target.value })} required />
                    <input type="number" placeholder="Quantity" value={formData.quantity} onChange={(e) => setFormData({ ...formData, quantity: e.target.value })} required />
                    <input type="text" placeholder="Unit" value={formData.unit} onChange={(e) => setFormData({ ...formData, unit: e.target.value })} required />
                    <input type="text" placeholder="Details" value={formData.productDetails} onChange={(e) => setFormData({ ...formData, productDetails: e.target.value })} required />
                    <input type="date" value={formData.refillDate} onChange={(e) => setFormData({ ...formData, refillDate: e.target.value })} required />
                    <input type="text" placeholder="Status" value={formData.status} onChange={(e) => setFormData({ ...formData, status: e.target.value })} required />
                    <input type="text" placeholder="Notes" value={formData.notes} onChange={(e) => setFormData({ ...formData, notes: e.target.value })} required />
                    <button type="submit">{editId ? "Update" : "Submit"}</button>
                </form>
            )}
        </div>
    );
}
