import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

const ItemList = () => {
    const [categories, setCategories] = useState([]);
    const [editableCategory, setEditableCategory] = useState(null);
    const [formData, setFormData] = useState({
        itemname: "",
        quantity: "",
        unit: "",
        productDetails: "",
        refillDate: "",
        status: "",
        notes: ""
    });
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await axios.get("http://localhost:8070/category");
                setCategories(response.data);
            } catch (error) {
                console.error("Error fetching categories:", error);
            }
        };
        fetchCategories();
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const updateCategory = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8070/category/update/${editableCategory}`, formData);
            setCategories(categories.map(cat => (cat._id === editableCategory ? { ...cat, ...formData } : cat)));
            alert("Category updated successfully");
            setEditableCategory(null);
            setFormData({
                itemname: "",
                quantity: "",
                unit: "",
                productDetails: "",
                refillDate: "",
                status: "",
                notes: ""
            });
        } catch (error) {
            console.error("Error updating category:", error);
            alert("Failed to update category: " + (error.response ? error.response.data : error.message));
        }
    };

    const deleteCategory = async (id) => {
        try {
            await axios.delete(`http://localhost:8070/category/delete/${id}`);
            setCategories(categories.filter(cat => cat._id !== id));
            alert("Category deleted successfully");
        } catch (error) {
            console.error("Error deleting category:", error);
            alert("Failed to delete category: " + (error.response ? error.response.data : error.message));
        }
    };

    const editCategory = (category) => {
        setEditableCategory(category._id);
        setFormData(category);
    };

    const generatePDF = async () => {
        const input = document.getElementById('category-table');
        const canvas = await html2canvas(input);
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF();
        const imgWidth = 210;
        const pageHeight = pdf.internal.pageSize.height;
        const imgHeight = (canvas.height * imgWidth) / canvas.width;
        let heightLeft = imgHeight;

        let position = 0;

        pdf.addImage(imgData, 'PNG', 10, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;

        while (heightLeft >= 0) {
            position = heightLeft - imgHeight;
            pdf.addPage();
            pdf.addImage(imgData, 'PNG', 10, position, imgWidth, imgHeight);
            heightLeft -= pageHeight;
        }

        pdf.save('categories-report.pdf');
    };

    const filteredCategories = categories.filter(category =>
        category.itemname.toLowerCase().includes(searchTerm.toLowerCase()) ||
        category.status.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div>
            <h2>Category Management</h2>
            <button onClick={generatePDF}>Generate PDF Report</button>
            <input
                type="text"
                placeholder="Search by Item Name or Status"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />

            {filteredCategories.length > 0 ? (
                <>
                    <table id="category-table">
                        <thead>
                            <tr>
                                <th>Item Name</th>
                                <th>Quantity</th>
                                <th>Unit</th>
                                <th>Product Details</th>
                                <th>Refill Date</th>
                                <th>Status</th>
                                <th>Notes</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredCategories.map((category) => (
                                <tr key={category._id}>
                                    <td>{category.itemname}</td>
                                    <td>{category.quantity}</td>
                                    <td>{category.unit}</td>
                                    <td>{category.productDetails}</td>
                                    <td>{category.refillDate}</td>
                                    <td>{category.status}</td>
                                    <td>{category.notes}</td>
                                    <td>
                                        <button onClick={() => editCategory(category)}>Edit</button>
                                        <button onClick={() => deleteCategory(category._id)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {editableCategory && (
                        <div>
                            <h3>Edit Category</h3>
                            <form onSubmit={updateCategory}>
                                <input type="text" name="itemname" value={formData.itemname} onChange={handleChange} placeholder="Item Name" required />
                                <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} placeholder="Quantity" required />
                                <input type="text" name="unit" value={formData.unit} onChange={handleChange} placeholder="Unit" required />
                                <input type="text" name="productDetails" value={formData.productDetails} onChange={handleChange} placeholder="Product Details" required />
                                <input type="date" name="refillDate" value={formData.refillDate} onChange={handleChange} placeholder="Refill Date" required />
                                <input type="text" name="status" value={formData.status} onChange={handleChange} placeholder="Status" required />
                                <textarea name="notes" value={formData.notes} onChange={handleChange} placeholder="Notes"></textarea>
                                <button type="submit">Update</button>
                            </form>
                        </div>
                    )}
                </>
            ) : (
                <p>No categories found</p>
            )}
        </div>
    );
};

export default ItemList;
