import React, { useState, useEffect } from "react";
import "./AddFruits.css";
import axios from "axios";

export default function AddFruits() {
    const [formData, setFormData] = useState({
        itemName: "",
        quantity: "",
        unit: "",
        productDetails: "",
        refillDate: "",
        status: "",
        notes: "",
    });

    const [errors, setErrors] = useState({});
    const [products, setProducts] = useState([]);
    const [formVisible, setFormVisible] = useState(false);
    const [updateMode, setUpdateMode] = useState(false); // To track if it's an update or add
    const [currentProductId, setCurrentProductId] = useState(null); // Store the current product ID for updating

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8071/category");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    const validateForm = () => {
        const newErrors = {};
        if (!formData.itemName?.trim()) newErrors.itemName = "Required.";
        if (!formData.quantity?.trim() || isNaN(formData.quantity) || formData.quantity <= 0) 
            newErrors.quantity = "Enter a valid number.";
        if (!formData.unit?.trim()) newErrors.unit = "Required.";
        if (!formData.productDetails?.trim()) newErrors.productDetails = "Required.";
        if (!formData.refillDate?.trim()) newErrors.refillDate = "Required.";
        if (!formData.status?.trim()) newErrors.status = "Required.";
        if (!formData.notes?.trim()) newErrors.notes = "Required.";
        return newErrors;
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const sendData = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        try {
            if (updateMode) {
                // Update the product
                await axios.put(`http://localhost:8071/category/update/${currentProductId}`, formData);
                alert("Product Updated Successfully");
            } else {
                // Add a new product
                await axios.post("http://localhost:8071/category/add", formData);
                alert("New Product Added Successfully");
            }
            setFormData({ itemName: "", quantity: "", unit: "", productDetails: "", refillDate: "", status: "", notes: "" });
            setErrors({});
            setUpdateMode(false); // Reset update mode after submission
            setCurrentProductId(null); // Reset product ID
            fetchProducts();
            setFormVisible(false);
        } catch (err) {
            alert(err.response?.data?.message || "Error occurred, try again.");
        }
    };

    const handleDelete = async (productId) => {
        try {
            await axios.delete(`http://localhost:8071/category/delete/${productId}`);
            alert("Product Deleted Successfully");
            fetchProducts();
        } catch (error) {
            console.error("Error deleting product:", error);
            alert("Error deleting product, try again.");
        }
    };

    const handleUpdate = (product) => {
        setFormData({
            itemName: product.itemname,
            quantity: product.quantity,
            unit: product.unit,
            productDetails: product.productDetails,
            refillDate: product.refillDate,
            status: product.status,
            notes: product.notes,
        });
        setUpdateMode(true);
        setCurrentProductId(product.id);
        setFormVisible(true); // Show the form for updating
    };

    return (
        <div className="container">
            <h1>Fruit List üçé</h1>
            <table>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Qty</th>
                        <th>Unit</th>
                        <th>Details</th>
                        <th>Refill Date</th>
                        <th>Status</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product, index) => (
                            <tr key={index}>
                                <td>{product.itemname}</td>
                                <td>{product.quantity}</td>
                                <td>{product.unit}</td>
                                <td>{product.productDetails}</td>
                                <td>{product.refillDate}</td>
                                <td>{product.status}</td>
                                <td>{product.notes}</td>
                                <td>
                                    <button onClick={() => handleUpdate(product)}>Update</button>
                                    <button onClick={() => handleDelete(product.id)}>Delete</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="8">No products available</td>
                        </tr>
                    )}
                </tbody>
            </table>

            <button onClick={() => setFormVisible(!formVisible)} className="add-button">
                {formVisible ? "Cancel" : "+ Add Product"}
            </button>

            {formVisible && (
                <form onSubmit={sendData} className="product-form">
                    <h2>{updateMode ? "Update Product" : "Add Product"}</h2>
                    {["itemName", "quantity", "unit", "productDetails", "refillDate", "status", "notes"].map((field) => (
                        <div key={field}>
                            <label>{field.replace(/([A-Z])/g, " $1")}</label>
                            <input 
                                type={field === "quantity" ? "number" : field === "refillDate" ? "date" : "text"}
                                name={field}
                                value={formData[field]}
                                onChange={handleChange}
                            />
                            {errors[field] && <p className="error">{errors[field]}</p>}
                        </div>
                    ))}
                    <button type="submit">{updateMode ? "Update" : "Submit"}</button>
                </form>
            )}
        </div>
    );
}
