import React, { useState, useEffect } from "react";
import "./AddFruits.css";
import axios from "axios";

export default function AddFruits() {
    const [itemName, setItemName] = useState("");
    const [quantity, setQuantity] = useState("");
    const [unit, setUnit] = useState("");
    const [productDetails, setProductDetails] = useState("");
    const [refillDate, setRefillDate] = useState("");
    const [status, setStatus] = useState("");
    const [notes, setNotes] = useState("");
    const [errors, setErrors] = useState({});
    const [products, setProducts] = useState([]);
    const [formVisible, setFormVisible] = useState(false);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8074/category");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    const validateForm = () => {
        const newErrors = {};
        if (!itemName.trim()) newErrors.itemName = "Item name is required.";
        if (!quantity.trim() || isNaN(quantity) || quantity <= 0) newErrors.quantity = "Quantity must be a positive number.";
        if (!unit.trim()) newErrors.unit = "Unit is required.";
        if (!productDetails.trim()) newErrors.productDetails = "Product details are required.";
        if (!refillDate.trim()) newErrors.refillDate = "Refill date is required.";
        if (!status.trim()) newErrors.status = "Status is required.";
        if (!notes.trim()) newErrors.notes = "Notes are required.";
        return newErrors;
    };

    const sendData = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();

        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        const newProduct = {
            itemname: itemName.trim(),
            quantity: quantity.trim(),
            unit: unit.trim(),
            productDetails: productDetails.trim(),
            refillDate: refillDate.trim(),
            status: status.trim(),
            notes: notes.trim(),
        };

        try {
            await axios.post("http://localhost:8074/category/add", newProduct);
            alert("New Product Added Successfully");
            setItemName("");
            setQuantity("");
            setUnit("");
            setProductDetails("");
            setRefillDate("");
            setStatus("");
            setNotes("");
            setErrors({});
            fetchProducts();
        } catch (err) {
            alert(err.response?.data?.message || "An error occurred. Please try again.");
        }
    };

    return (
        <div className="add-fruits-page">
            <div className="container">
                <h1>Fruit List</h1>
                <table>
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Quantity</th>
                            <th>Unit</th>
                            <th>Product Details</th>
                            <th>Refill Date</th>
                            <th>Status</th>
                            <th>Notes</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.length > 0 ? (
                            products.map((product, index) => (
                                <tr key={index}>
                                    <td>{product.itemname}</td>
                                    <td>{product.quantity}</td>
                                    <td>{product.unit}</td>
                                    <td>{product.productDetails}</td>
                                    <td>{product.refillDate}</td>
                                    <td>{product.status}</td>
                                    <td>{product.notes}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="7">No products available</td>
                            </tr>
                        )}
                    </tbody>
                </table>

                <button onClick={() => setFormVisible(!formVisible)} className="add-button">
                    {formVisible ? "Cancel" : "+ Add Product"}
                </button>

                {formVisible && (
                    <form onSubmit={sendData} className="product-form">
                        <h2>Add Product</h2>

                        <label>Item Name:</label>
                        <input type="text" value={itemName} onChange={(e) => setItemName(e.target.value)} />
                        {errors.itemName && <p className="error">{errors.itemName}</p>}

                        <label>Quantity:</label>
                        <input type="text" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
                        {errors.quantity && <p className="error">{errors.quantity}</p>}

                        <label>Unit:</label>
                        <input type="text" value={unit} onChange={(e) => setUnit(e.target.value)} />
                        {errors.unit && <p className="error">{errors.unit}</p>}

                        <label>Product Details:</label>
                        <input type="text" value={productDetails} onChange={(e) => setProductDetails(e.target.value)} />
                        {errors.productDetails && <p className="error">{errors.productDetails}</p>}

                        <label>Refill Date:</label>
                        <input type="date" value={refillDate} onChange={(e) => setRefillDate(e.target.value)} />
                        {errors.refillDate && <p className="error">{errors.refillDate}</p>}

                        <label>Status:</label>
                        <input type="text" value={status} onChange={(e) => setStatus(e.target.value)} />
                        {errors.status && <p className="error">{errors.status}</p>}

                        <label>Notes:</label>
                        <input type="text" value={notes} onChange={(e) => setNotes(e.target.value)} />
                        {errors.notes && <p className="error">{errors.notes}</p>}

                        <button type="submit">Submit</button>
                    </form>
                )}
            </div>
        </div>
    );
}
