import React, { useState, useEffect } from "react";
import "./AddFruits.css";
import axios from "axios";

export default function AddFruits() {
    const [formData, setFormData] = useState({
        itemName: "",
        quantity: "",
        unit: "",
        productDetails: "",
        refillDate: "",
        status: "",
        notes: "",
    });

    const [errors, setErrors] = useState({});
    const [products, setProducts] = useState([]);
    const [formVisible, setFormVisible] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null); // Track editing item

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8071/category");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    const validateForm = () => {
        const newErrors = {};
        if (!formData.itemName.trim()) newErrors.itemName = "Required.";
        if (!formData.quantity.trim() || isNaN(formData.quantity) || formData.quantity <= 0) 
            newErrors.quantity = "Enter a valid number.";
        if (!formData.unit.trim()) newErrors.unit = "Required.";
        if (!formData.productDetails.trim()) newErrors.productDetails = "Required.";
        if (!formData.refillDate.trim()) newErrors.refillDate = "Required.";
        if (!formData.status.trim()) newErrors.status = "Required.";
        if (!formData.notes.trim()) newErrors.notes = "Required.";
        return newErrors;
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const sendData = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        try {
            if (editingProduct) {
                await axios.put(`http://localhost:8071/category/update/${editingProduct._id}`, formData);
                alert("Product Updated Successfully");
            } else {
                await axios.post("http://localhost:8071/category/add", formData);
                alert("New Product Added Successfully");
            }

            setFormData({ itemName: "", quantity: "", unit: "", productDetails: "", refillDate: "", status: "", notes: "" });
            setErrors({});
            setFormVisible(false);
            setEditingProduct(null);
            fetchProducts();
        } catch (err) {
            alert(err.response?.data?.message || "Error occurred, try again.");
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this product?")) {
            try {
                await axios.delete(`http://localhost:8071/category/delete/${id}`);
                alert("Product Deleted Successfully");
                fetchProducts();
            } catch (err) {
                alert("Error deleting product");
            }
        }
    };

    const handleUpdate = (product) => {
        setFormData(product);
        setEditingProduct(product);
        setFormVisible(true);
    };

    return (
        <div className="container">
            <h1>Fruit List 🍎</h1>
            <table>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Qty</th>
                        <th>Unit</th>
                        <th>Details</th>
                        <th>Refill Date</th>
                        <th>Status</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product, index) => (
                            <tr key={index}>
                                <td>{product.itemName}</td>
                                <td>{product.quantity}</td>
                                <td>{product.unit}</td>
                                <td>{product.productDetails}</td>
                                <td>{product.refillDate}</td>
                                <td>{product.status}</td>
                                <td>{product.notes}</td>
                                <td>
                                    <button onClick={() => handleUpdate(product)} className="update-button">Update</button>
                                    <button onClick={() => handleDelete(product._id)} className="delete-button">Delete</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="8">No products available</td>
                        </tr>
                    )}
                </tbody>
            </table>

            <button onClick={() => { setFormVisible(!formVisible); setEditingProduct(null); }} className="add-button">
                {formVisible ? "Cancel" : "+ Add Product"}
            </button>

            {formVisible && (
                <form onSubmit={sendData} className="product-form">
                    <h2>{editingProduct ? "Update Product" : "Add Product"}</h2>
                    {["itemName", "quantity", "unit", "productDetails", "refillDate", "status", "notes"].map((field) => (
                        <div key={field}>
                            <label>{field.replace(/([A-Z])/g, " $1")}</label>
                            <input type={field === "quantity" ? "number" : field === "refillDate" ? "date" : "text"}
                                name={field}
                                value={formData[field]}
                                onChange={handleChange}
                            />
                            {errors[field] && <p className="error">{errors[field]}</p>}
                        </div>
                    ))}
                    <button type="submit">{editingProduct ? "Update" : "Submit"}</button>
                </form>
            )}
        </div>
    );
}
