import React, { useState, useEffect } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "./AddFruits.css";

export default function AddFruits() {
    const [products, setProducts] = useState([]);
    const [editingProduct, setEditingProduct] = useState(null);
    const [formVisible, setFormVisible] = useState(false);
    const [editFormVisible, setEditFormVisible] = useState(false);
    const [formData, setFormData] = useState({
        itemname: "",
        quantity: "",
        unit: "",
        productDetails: "",
        refillDate: "",
        status: "",
        notes: ""
    });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8073/category");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleAddProduct = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:8073/category/add", formData);
            alert("New Product Added Successfully");
            setFormData({
                itemname: "",
                quantity: "",
                unit: "",
                productDetails: "",
                refillDate: "",
                status: "",
                notes: ""
            });
            fetchProducts();
            setFormVisible(false);
        } catch (err) {
            alert(err.response?.data?.message || "An error occurred. Please try again.");
        }
    };

    const handleEdit = (product) => {
        setEditingProduct(product);
        setFormData(product);
        setEditFormVisible(true);
    };

    const handleUpdateProduct = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8073/category/update/${editingProduct._id}`, formData);
            alert("Product Updated Successfully");
            fetchProducts();
            setEditingProduct(null);
            setEditFormVisible(false);
        } catch (err) {
            alert("Error updating product");
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this product?")) {
            try {
                await axios.delete(`http://localhost:8073/category/delete/${id}`);
                alert("Product Deleted Successfully");
                fetchProducts();
            } catch (err) {
                alert("Error deleting product");
            }
        }
    };

    const generatePDF = () => {
        const doc = new jsPDF();

        // Get the data from the table
        const tableData = products.map((product) => [
            product.itemname,
            product.quantity,
            product.unit,
            product.productDetails,
            product.refillDate,
            product.status,
            product.notes,
        ]);

        // Add the table to the PDF
        doc.autoTable({
            head: [
                ["Item Name", "Quantity", "Unit", "Product Details", "Refill Date", "Status", "Notes"]
            ],
            body: tableData,
        });

        // Save the PDF
        doc.save("product_report.pdf");
    };

    return (
        <div className="container">
            <h2>Product List</h2>
            <table>
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Quantity</th>
                        <th>Unit</th>
                        <th>Product Details</th>
                        <th>Refill Date</th>
                        <th>Status</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product._id}>
                            <td>{product.itemname}</td>
                            <td>{product.quantity}</td>
                            <td>{product.unit}</td>
                            <td>{product.productDetails}</td>
                            <td>{product.refillDate}</td>
                            <td>{product.status}</td>
                            <td>{product.notes}</td>
                            <td>
                                <button className="edit-button" onClick={() => handleEdit(product)}>Edit</button>
                                <button className="delete-button" onClick={() => handleDelete(product._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <button onClick={() => setFormVisible(!formVisible)} className="add-button">
                {formVisible ? "Cancel" : "Add Product"}
            </button>

            {formVisible && (
                <form onSubmit={handleAddProduct}>
                    <h2>Add Product</h2>
                    {Object.keys(formData).map((key) => (
                        <div key={key}>
                            <label>{key}</label>
                            <input type="text" name={key} value={formData[key]} onChange={handleInputChange} required />
                        </div>
                    ))}
                    <button type="submit">Submit</button>
                </form>
            )}

            {editFormVisible && (
                <form onSubmit={handleUpdateProduct}>
                    <h2>Update Product</h2>
                    {Object.keys(formData).map((key) => (
                        <div key={key}>
                            <label>{key}</label>
                            <input type="text" name={key} value={formData[key]} onChange={handleInputChange} required />
                        </div>
                    ))}
                    <button type="submit">Update</button>
                    <button className="cancel-button" onClick={() => setEditFormVisible(false)}>Cancel</button>
                </form>
            )}

            <button onClick={generatePDF} className="generate-pdf-button">Generate Report</button>
        </div>
    );
}
