import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar, Pie } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from "chart.js";
import { useNavigate } from "react-router-dom";
import "./ProductCharts.css"; 

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const ProductCharts = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get("http://localhost:8073/category/add");
                setProducts(response.data);
            } catch (error) {
                console.error("Error fetching product data:", error);
            }
        };

        fetchProducts();
    }, []);

    const productStatusCount = products.reduce((acc, product) => {
        const status = product.status;
        acc[status] = acc[status] ? acc[status] + 1 : 1;
        return acc;
    }, {});

    const chartLabels = Object.keys(productStatusCount);
    const chartData = Object.values(productStatusCount);

    const barChartData = {
        labels: chartLabels,
        datasets: [
            {
                label: "Product Status",
                data: chartData,
                backgroundColor: "rgba(54, 162, 235, 0.6)",
                borderColor: "rgba(54, 162, 235, 1)",
                borderWidth: 3,
            },
        ],
    };

    const pieChartData = {
        labels: chartLabels,
        datasets: [
            {
                data: chartData,
                backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#9966FF"],
                hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#9966FF"],
            },
        ],
    };

    return (
        <div>
            <button onClick={() => navigate("/")} className="back-button">Back to Products</button>
            <h2 className="chart-heading">Product Status Overview</h2>
            <div className="chart-container">
                <Bar data={barChartData} />
            </div>

            <h2 className="chart-heading">Product Status Distribution</h2>
            <div className="chart-container">
                <Pie data={pieChartData} />
            </div>
        </div>
    );
};

export default ProductCharts;
