import React, { useState } from 'react';
import './CategoryList.css';

const categories = [
    { id: 1, name: "Fruits", image: "/images/fruits.jpg" },
    { id: 2, name: "Vegetables", image: "/images/vegetables.jpg" },
    { id: 3, name: "Dairy", image: "/images/dairy.jpg" },
    { id: 4, name: "Meat", image: "/images/meat.jpg" },
    { id: 5, name: "Grains", image: "/images/grains.jpg" },
    { id: 6, name: "Snacks", image: "/images/snacks.jpg" },
    { id: 7, name: "Drinks", image: "/images/drinks.jpg" }
];

const CategoryList = () => {
    const [selectedCategory, setSelectedCategory] = useState(null);

    return (
        <div className="container">
            <h1 className="title">CATEGORIES</h1>
            <div className="category-grid">
                {categories.map(category => (
                    <div 
                        key={category.id} 
                        className={`category-card ${selectedCategory === category.id ? 'selected' : ''}`}
                        onClick={() => setSelectedCategory(category.id)}
                    >
                        <img src={category.image} alt={category.name} className="category-image" />
                        <div className="category-name">
                            {category.name} <span className="arrow">&gt;</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CategoryList;
