import React, { useState, useEffect } from "react";
import "./AddFruits.css";

export default function AddFruits() {
    const [formData, setFormData] = useState({
        itemName: "",
        quantity: "",
        unit: "",
        productDetails: "",
        refillDate: "",
        status: "",
        notes: "",
    });

    const [errors, setErrors] = useState({});
    const [products, setProducts] = useState([]);
    const [formVisible, setFormVisible] = useState(false);
    const [isUpdateMode, setIsUpdateMode] = useState(false);
    const [productToUpdate, setProductToUpdate] = useState(null);

    // Fetch products from the server
    const fetchProducts = async () => {
        try {
            const response = await fetch("http://localhost:8078/category");
            const data = await response.json();
            setProducts(data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    // Validate form data
    const validateForm = () => {
        const newErrors = {};

        if (!formData.itemName?.trim()) newErrors.itemName = "Required.";
        if (isNaN(formData.quantity) || formData.quantity <= 0) newErrors.quantity = "Enter a valid number.";
        if (!formData.unit?.trim()) newErrors.unit = "Required.";
        if (!formData.productDetails?.trim()) newErrors.productDetails = "Required.";
        if (!formData.refillDate?.trim()) newErrors.refillDate = "Required.";
        if (!formData.status?.trim()) newErrors.status = "Required.";
        if (!formData.notes?.trim()) newErrors.notes = "Required.";

        return newErrors;
    };

    // Handle form data changes
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Submit form data to the server
    const sendData = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        try {
            if (isUpdateMode) {
                const response = await fetch(`http://localhost:8078/category/update/${productToUpdate._id}`, {
                    method: 'PUT',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(formData),
                });
                if (response.ok) {
                    alert("Product Updated Successfully");
                }
            } else {
                const response = await fetch("http://localhost:8078/category/add", {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(formData),
                });
                if (response.ok) {
                    alert("New Product Added Successfully");
                }
            }
            resetForm();
            fetchProducts(); // Refresh the product list
        } catch (err) {
            console.error("Error occurred:", err);
            alert("Network or server error. Please try again later.");
        }
    };

    // Reset the form data
    const resetForm = () => {
        setFormData({
            itemName: "",
            quantity: "",
            unit: "",
            productDetails: "",
            refillDate: "",
            status: "",
            notes: "",
        });
        setErrors({});
        setFormVisible(false);
        setIsUpdateMode(false);
        setProductToUpdate(null);
    };

    // Handle delete product
    const handleDelete = async (productId) => {
        try {
            const response = await fetch(`http://localhost:8078/category/delete/${productId}`, {
                method: 'DELETE',
            });
            if (response.ok) {
                alert("Product deleted successfully");
                fetchProducts();
            }
        } catch (err) {
            alert("Error deleting product:", err.message);
        }
    };

    // Handle update product
    const handleUpdate = (product) => {
        setProductToUpdate(product);
        setFormData({
            itemName: product.itemname,
            quantity: product.quantity,
            unit: product.unit,
            productDetails: product.productDetails,
            refillDate: product.refillDate,
            status: product.status,
            notes: product.notes,
        });
        setIsUpdateMode(true);
        setFormVisible(true);
    };

    return (
        <div className="container">
            <h1>Fruit List üçé</h1>
            <table>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Qty</th>
                        <th>Unit</th>
                        <th>Details</th>
                        <th>Refill Date</th>
                        <th>Status</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product, index) => (
                            <tr key={index}>
                                <td>{product.itemname}</td>
                                <td>{product.quantity}</td>
                                <td>{product.unit}</td>
                                <td>{product.productDetails}</td>
                                <td>{product.refillDate}</td>
                                <td>{product.status}</td>
                                <td>{product.notes}</td>
                                <td>
                                    <button onClick={() => handleUpdate(product)}>Update</button>
                                    <button onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="8">No products available</td>
                        </tr>
                    )}
                </tbody>
            </table>

            <button onClick={() => setFormVisible(!formVisible)} className="add-button">
                {formVisible ? "Cancel" : "+ Add Product"}
            </button>

            {formVisible && (
                <form onSubmit={sendData} className="product-form">
                    <h2>{isUpdateMode ? "Update Product" : "Add Product"}</h2>
                    {["itemName", "quantity", "unit", "productDetails", "refillDate", "status", "notes"].map((field) => (
                        <div key={field}>
                            <label>{field.replace(/([A-Z])/g, " $1")}</label>
                            <input 
                                type={field === "quantity" ? "number" : field === "refillDate" ? "date" : "text"}
                                name={field}
                                value={formData[field]}
                                onChange={handleChange}
                            />
                            {errors[field] && <p className="error">{errors[field]}</p>}
                        </div>
                    ))}
                    <button type="submit">{isUpdateMode ? "Update Product" : "Submit"}</button>
                </form>
            )}
        </div>
    );
}
