import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AddFruits.css";

export default function AddFruits() {
    const [formData, setFormData] = useState({
        itemName: "",
        quantity: "",
        unit: "",
        productDetails: "",
        refillDate: "",
        status: "",
        notes: "",
    });

    const [errors, setErrors] = useState({});
    const [products, setProducts] = useState([]);
    const [formVisible, setFormVisible] = useState(false);
    const [isUpdateMode, setIsUpdateMode] = useState(false);
    const [productToUpdate, setProductToUpdate] = useState(null);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8073/category");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.itemName?.trim()) newErrors.itemName = "Required.";
        if (isNaN(formData.quantity) || formData.quantity <= 0) newErrors.quantity = "Enter a valid number.";
        if (!formData.unit?.trim()) newErrors.unit = "Required.";
        if (!formData.productDetails?.trim()) newErrors.productDetails = "Required.";
        if (!formData.refillDate?.trim()) newErrors.refillDate = "Required.";
        if (!formData.status?.trim()) newErrors.status = "Required.";
        if (!formData.notes?.trim()) newErrors.notes = "Required.";
        return newErrors;
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const sendData = async (e) => {
        e.preventDefault();
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        console.log("Submitting data:", formData); // Add logging here to check the form data

        try {
            if (isUpdateMode) {
                // Update Product
                await axios.put(`http://localhost:8073/category/update/${productToUpdate._id}`, formData);
                alert("Product Updated Successfully");
            } else {
                // Add New Product
                const response = await axios.post("http://localhost:8073/category/add", formData);
                console.log("Response after adding product:", response); // Log the response here
                alert("New Product Added Successfully");
            }
            resetForm();
            fetchProducts();
        } catch (err) {
            console.error("Error occurred:", err);
            alert(err.response?.data?.message || "Error occurred, try again.");
        }
    };

    const resetForm = () => {
        setFormData({
            itemName: "",
            quantity: "",
            unit: "",
            productDetails: "",
            refillDate: "",
            status: "",
            notes: "",
        });
        setErrors({});
        setFormVisible(false);
        setIsUpdateMode(false);
        setProductToUpdate(null);
    };

    const handleDelete = (productId) => {
        axios.delete(`http://localhost:8073/category/delete/${productId}`)
            .then(() => {
                alert("Product deleted successfully");
                fetchProducts();
            })
            .catch((err) => {
                alert("Error deleting product:", err.message);
            });
    };

    const handleUpdate = (product) => {
        setProductToUpdate(product);
        setFormData({
            itemName: product.itemname,
            quantity: product.quantity,
            unit: product.unit,
            productDetails: product.productDetails,
            refillDate: product.refillDate,
            status: product.status,
            notes: product.notes,
        });
        setIsUpdateMode(true);
        setFormVisible(true);
    };

    return (
        <div className="container">
            <h1>Fruit List üçé</h1>
            <table>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Qty</th>
                        <th>Unit</th>
                        <th>Details</th>
                        <th>Refill Date</th>
                        <th>Status</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product, index) => (
                            <tr key={index}>
                                <td>{product.itemname}</td>
                                <td>{product.quantity}</td>
                                <td>{product.unit}</td>
                                <td>{product.productDetails}</td>
                                <td>{product.refillDate}</td>
                                <td>{product.status}</td>
                                <td>{product.notes}</td>
                                <td>
                                    <button onClick={() => handleUpdate(product)}>Update</button>
                                    <button onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="8">No products available</td>
                        </tr>
                    )}
                </tbody>
            </table>

            <button onClick={() => setFormVisible(!formVisible)} className="add-button">
                {formVisible ? "Cancel" : "+ Add Product"}
            </button>

            {formVisible && !isUpdateMode && (
                <form onSubmit={sendData} className="product-form">
                    <h2>Add Product</h2>
                    {["itemName", "quantity", "unit", "productDetails", "refillDate", "status", "notes"].map((field) => (
                        <div key={field}>
                            <label>{field.replace(/([A-Z])/g, " $1")}</label>
                            <input 
                                type={field === "quantity" ? "number" : field === "refillDate" ? "date" : "text"}
                                name={field}
                                value={formData[field]}
                                onChange={handleChange}
                            />
                            {errors[field] && <p className="error">{errors[field]}</p>}
                        </div>
                    ))}
                    <button type="submit">Submit</button>
                </form>
            )}

            {formVisible && isUpdateMode && (
                <form onSubmit={sendData} className="product-form">
                    <h2>Update Product</h2>
                    {["itemName", "quantity", "unit", "productDetails", "refillDate", "status", "notes"].map((field) => (
                        <div key={field}>
                            <label>{field.replace(/([A-Z])/g, " $1")}</label>
                            <input 
                                type={field === "quantity" ? "number" : field === "refillDate" ? "date" : "text"}
                                name={field}
                                value={formData[field]}
                                onChange={handleChange}
                            />
                            {errors[field] && <p className="error">{errors[field]}</p>}
                        </div>
                    ))}
                    <button type="submit">Update Product</button>
                </form>
            )}
        </div>
    );
}
