import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddFruits.css'; // Ensure this CSS file exists

export default function FruitManagement() {
    const [fruits, setFruits] = useState([]);
    const [itemName, setItemName] = useState("");
    const [quantity, setQuantity] = useState("");
    const [unit, setUnit] = useState("");
    const [productDetails, setProductDetails] = useState("");
    const [refillDate, setRefillDate] = useState("");
    const [status, setStatus] = useState("");
    const [notes, setNotes] = useState("");
    const [editId, setEditId] = useState(null); // Track the ID of fruit being edited
    const [errors, setErrors] = useState({});

    // Fetch fruit details from the database
    useEffect(() => {
        axios.get("http://localhost:8070/product")
            .then(response => setFruits(response.data))
            .catch(error => console.error("Error fetching data:", error));
    }, []);

    // Validation logic
    const validateForm = () => {
        const newErrors = {};
        if (!itemName.trim()) newErrors.itemName = "Item name is required.";
        if (!quantity.trim() || isNaN(quantity) || quantity <= 0) newErrors.quantity = "Quantity must be a positive number.";
        if (!unit.trim()) newErrors.unit = "Unit is required.";
        if (!productDetails.trim()) newErrors.productDetails = "Product details are required.";
        if (!refillDate.trim()) newErrors.refillDate = "Refill date is required.";
        if (!status.trim()) newErrors.status = "Status is required.";
        if (!notes.trim()) newErrors.notes = "Notes are required.";
        return newErrors;
    };

    // Handle adding or updating fruit details
    const handleSubmit = (e) => {
        e.preventDefault();
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        const newFruit = { itemName, quantity, unit, productDetails, refillDate, status, notes };

        if (editId) {
            // Update existing fruit
            axios.put(`http://localhost:8070/product/update/${editId}`, newFruit)
                .then(() => {
                    alert("Product Updated Successfully");
                    setFruits(fruits.map(fruit => fruit._id === editId ? { ...fruit, ...newFruit } : fruit));
                    resetForm();
                })
                .catch(err => alert("Error updating product: " + err));
        } else {
            // Add new fruit
            axios.post("http://localhost:8070/product/add", newFruit)
                .then(response => {
                    alert("New Product Added Successfully");
                    setFruits([...fruits, response.data]);
                    resetForm();
                })
                .catch(err => alert("Error adding product: " + err));
        }
    };

    // Handle editing fruit
    const handleEdit = (fruit) => {
        setItemName(fruit.itemName);
        setQuantity(fruit.quantity);
        setUnit(fruit.unit);
        setProductDetails(fruit.productDetails);
        setRefillDate(fruit.refillDate);
        setStatus(fruit.status);
        setNotes(fruit.notes);
        setEditId(fruit._id);
    };

    // Handle deleting fruit
    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this product?")) {
            axios.delete(`http://localhost:8070/product/delete/${id}`)
                .then(() => {
                    alert("Product Deleted Successfully");
                    setFruits(fruits.filter(fruit => fruit._id !== id));
                })
                .catch(err => alert("Error deleting product: " + err));
        }
    };

    // Reset form fields
    const resetForm = () => {
        setItemName("");
        setQuantity("");
        setUnit("");
        setProductDetails("");
        setRefillDate("");
        setStatus("");
        setNotes("");
        setEditId(null);
        setErrors({});
    };

    return (
        <div className="container">
            <h2>Fruit Inventory</h2>

            {/* Fruit Details Table */}
            <table>
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Quantity</th>
                        <th>Unit</th>
                        <th>Product Details</th>
                        <th>Refill Date</th>
                        <th>Status</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {fruits.map((fruit) => (
                        <tr key={fruit._id}>
                            <td>{fruit.itemName}</td>
                            <td>{fruit.quantity}</td>
                            <td>{fruit.unit}</td>
                            <td>{fruit.productDetails}</td>
                            <td>{fruit.refillDate}</td>
                            <td>{fruit.status}</td>
                            <td>{fruit.notes}</td>
                            <td>
                                <button onClick={() => handleEdit(fruit)}>Edit</button>
                                <button onClick={() => handleDelete(fruit._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Add/Edit Fruit Form */}
            <form onSubmit={handleSubmit}>
                <h2>{editId ? "Edit Product" : "Add Product"}</h2>

                <label>Item Name:</label>
                <input type="text" value={itemName} onChange={(e) => setItemName(e.target.value)} />
                {errors.itemName && <p className="error">{errors.itemName}</p>}

                <label>Quantity:</label>
                <input type="text" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
                {errors.quantity && <p className="error">{errors.quantity}</p>}

                <label>Unit:</label>
                <input type="text" value={unit} onChange={(e) => setUnit(e.target.value)} />
                {errors.unit && <p className="error">{errors.unit}</p>}

                <label>Product Details:</label>
                <input type="text" value={productDetails} onChange={(e) => setProductDetails(e.target.value)} />
                {errors.productDetails && <p className="error">{errors.productDetails}</p>}

                <label>Refill Date:</label>
                <input type="date" value={refillDate} onChange={(e) => setRefillDate(e.target.value)} />
                {errors.refillDate && <p className="error">{errors.refillDate}</p>}

                <label>Status:</label>
                <input type="text" value={status} onChange={(e) => setStatus(e.target.value)} />
                {errors.status && <p className="error">{errors.status}</p>}

                <label>Notes:</label>
                <input type="text" value={notes} onChange={(e) => setNotes(e.target.value)} />
                {errors.notes && <p className="error">{errors.notes}</p>}

                <button type="submit">{editId ? "Update" : "Submit"}</button>
                {editId && <button type="button" onClick={resetForm}>Cancel</button>}
            </form>
        </div>
    );
}
