import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CategoryList.css';

const categories = [
    { id: 1, name: "Fruits", image: "/images/fruits.jpg" },
    { id: 2, name: "Drinks", image: "/images/drinks.jpg" },
    { id: 3, name: "Dairy", image: "/images/dairy.jpg" },
    { id: 4, name: "Meat", image: "/images/meat.jpg" },
    { id: 5, name: "Vegetables", image: "/images/vegetables.jpg" },
    { id: 6, name: "Snacks", image: "/images/snacks.jpg" },
    { id: 7, name: "Grains", image: "/images/grains.jpg" }
];

const CategoryList = () => {
    const navigate = useNavigate();

    const handleCategoryClick = (categoryId) => {
        if (categoryId === 1) navigate("/add");
    };

    return (
        <div className="category-page">
            <h1 className="title">CATEGORIES</h1>
            <div className="category-grid">
                {categories.map(category => (
                    <div key={category.id} onClick={() => handleCategoryClick(category.id)} className="category-card">
                        <img src={category.image} alt={category.name} className="category-image" />
                        <div className="category-name">{category.name} &gt;</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CategoryList;
