import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AddFruits.css";

export default function AddFruits() {
    const [formData, setFormData] = useState({
        itemName: "",
        quantity: "",
        unit: "",
        productDetails: "",
        refillDate: "",
        status: "",
        notes: "",
    });

    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [editableProduct, setEditableProduct] = useState(null);
    const [errors, setErrors] = useState({}); // Used for displaying validation errors
    
    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8071/category");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.itemName.trim()) newErrors.itemName = "Item Name is required.";
        if (!formData.quantity.trim() || isNaN(formData.quantity) || formData.quantity <= 0) 
            newErrors.quantity = "Enter a valid quantity.";
        if (!formData.unit.trim()) newErrors.unit = "Unit is required.";
        if (!formData.productDetails.trim()) newErrors.productDetails = "Product details are required.";
        if (!formData.refillDate.trim()) newErrors.refillDate = "Refill date is required.";
        if (!formData.status.trim()) newErrors.status = "Status is required.";
        if (!formData.notes.trim()) newErrors.notes = "Notes are required.";

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const sendData = async (e) => {
        e.preventDefault();
        if (!validateForm()) return; // Prevent form submission if validation fails

        try {
            if (editableProduct) {
                await axios.put(`http://localhost:8071/category/update/${editableProduct}`, formData);
                alert("Product Updated Successfully");
            } else {
                await axios.post("http://localhost:8071/category/add", formData);
                alert("New Product Added Successfully");
            }
            setFormData({ itemName: "", quantity: "", unit: "", productDetails: "", refillDate: "", status: "", notes: "" });
            setEditableProduct(null);
            fetchProducts();
        } catch (error) {
            console.error("Error saving product:", error);
            alert("Failed to save product.");
        }
    };

    const editProduct = (product) => {
        setEditableProduct(product._id);
        setFormData(product);
    };

    const deleteProduct = async (id) => {
        try {
            await axios.delete(`http://localhost:8071/category/${id}`);
            alert("Product Deleted Successfully");
            fetchProducts();
        } catch (error) {
            console.error("Error deleting product:", error);
            alert("Failed to delete product.");
        }
    };

    const filteredProducts = products.filter(product =>
        product.itemName.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="container">
            <h1>Fruit Inventory Management</h1>
            
            <input 
                type="text" 
                placeholder="Search by Item Name" 
                value={searchTerm} 
                onChange={(e) => setSearchTerm(e.target.value)} 
                className="search-bar" 
            />
            
            <button onClick={() => setEditableProduct(null)}>Add New Product</button>
            
            {editableProduct !== null && (
                <form onSubmit={sendData} className="form-container">
                    <input 
                        type="text" 
                        name="itemName" 
                        value={formData.itemName} 
                        onChange={handleChange} 
                        placeholder="Item Name" 
                        required 
                    />
                    {errors.itemName && <span className="error">{errors.itemName}</span>}
                    
                    <input 
                        type="text" 
                        name="quantity" 
                        value={formData.quantity} 
                        onChange={handleChange} 
                        placeholder="Quantity" 
                        required 
                    />
                    {errors.quantity && <span className="error">{errors.quantity}</span>}
                    
                    <input 
                        type="text" 
                        name="unit" 
                        value={formData.unit} 
                        onChange={handleChange} 
                        placeholder="Unit" 
                        required 
                    />
                    {errors.unit && <span className="error">{errors.unit}</span>}
                    
                    <input 
                        type="text" 
                        name="productDetails" 
                        value={formData.productDetails} 
                        onChange={handleChange} 
                        placeholder="Product Details" 
                        required 
                    />
                    {errors.productDetails && <span className="error">{errors.productDetails}</span>}
                    
                    <input 
                        type="date" 
                        name="refillDate" 
                        value={formData.refillDate} 
                        onChange={handleChange} 
                        required 
                    />
                    {errors.refillDate && <span className="error">{errors.refillDate}</span>}
                    
                    <input 
                        type="text" 
                        name="status" 
                        value={formData.status} 
                        onChange={handleChange} 
                        placeholder="Status" 
                        required 
                    />
                    {errors.status && <span className="error">{errors.status}</span>}
                    
                    <input 
                        type="text" 
                        name="notes" 
                        value={formData.notes} 
                        onChange={handleChange} 
                        placeholder="Notes" 
                        required 
                    />
                    {errors.notes && <span className="error">{errors.notes}</span>}
                    
                    <button type="submit">{editableProduct ? "Update Product" : "Add Product"}</button>
                </form>
            )}
            
            {filteredProducts.length > 0 ? (
                <table className="product-table">
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Quantity</th>
                            <th>Unit</th>
                            <th>Product Details</th>
                            <th>Refill Date</th>
                            <th>Status</th>
                            <th>Notes</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredProducts.map((product) => (
                            <tr key={product._id}>
                                <td>{product.itemName}</td>
                                <td>{product.quantity}</td>
                                <td>{product.unit}</td>
                                <td>{product.productDetails}</td>
                                <td>{product.refillDate}</td>
                                <td>{product.status}</td>
                                <td>{product.notes}</td>
                                <td>
                                    <button onClick={() => editProduct(product)}>Edit</button>
                                    <button className="delete-button" onClick={() => deleteProduct(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No products found.</p>
            )}
        </div>
    );
}
