import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CategoryList = () => {
    const [categories, setCategories] = useState([]);
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        // Sample category data
        const sampleCategories = [
            { id: 1, name: "Fruits", hasSubcategories: true },
            { id: 2, name: "Vegetables", hasSubcategories: false },
            { id: 3, name: "Dairy", hasSubcategories: true },
            { id: 4, name: "Meat", hasSubcategories: false },
            { id: 5, name: "Grains", hasSubcategories: false },
            { id: 6, name: "Snacks", hasSubcategories: true },
            { id: 7, name: "Drinks", hasSubcategories: false }
        ];
        setCategories(sampleCategories);
    }, []);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="category-dropdown">
            <button onClick={toggleDropdown} className="category-button">
                Categories
            </button>
            {isOpen && (
                <ul className="dropdown-list">
                    {categories.map((category, index) => (
                        <li key={index} className="dropdown-item">
                            {category.name}
                            {category.hasSubcategories && (
                                <span className="category-arrow"> &gt; </span>
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default CategoryList;
